import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ToastService } from '../../services/toast.service';

interface RoutineStep {
  step: number;
  product: string;
  description: string;
}

@Component({
  selector: 'app-routine-builder',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './routine-builder.component.html'
})
export class RoutineBuilderComponent {
  private toastService = inject(ToastService);

  skinType = '';
  goal = '';
  budget = '';

  morningRoutine: RoutineStep[] = [];
  eveningRoutine: RoutineStep[] = [];

  activeTab: 'morning' | 'evening' = 'morning';
  showResults = false;

  generateRoutine(): void {
    if (!this.skinType || !this.goal || !this.budget) {
      alert('Please fill all fields');
      return;
    }

    this.morningRoutine = this.getMorningRoutine();
    this.eveningRoutine = this.getEveningRoutine();
    this.showResults = true;
    this.toastService.show('Routine generated ✨');
  }

  getMorningRoutine(): RoutineStep[] {
    const base: RoutineStep[] = [
      { step: 1, product: 'Cleanser', description: `Gentle ${this.skinType.toLowerCase()} skin cleanser` },
      { step: 2, product: 'Serum', description: this.getSerumForGoal() },
      { step: 3, product: 'Moisturizer', description: `Hydrating cream for ${this.skinType.toLowerCase()} skin` },
      { step: 4, product: 'SPF', description: 'Broad spectrum SPF 30+' }
    ];
    return base;
  }

  getEveningRoutine(): RoutineStep[] {
    const base: RoutineStep[] = [
      { step: 1, product: 'Cleanser', description: `Gentle ${this.skinType.toLowerCase()} skin cleanser` },
      { step: 2, product: 'Treatment', description: this.getTreatmentForGoal() },
      { step: 3, product: 'Serum', description: this.getSerumForGoal() },
      { step: 4, product: 'Night Cream', description: `Nourishing night cream for ${this.skinType.toLowerCase()} skin` }
    ];
    return base;
  }

  getSerumForGoal(): string {
    const serums: Record<string, string> = {
      'hydration': 'Hyaluronic Acid for deep hydration',
      'acne': 'Niacinamide 10% for blemish control',
      'anti-aging': 'Vitamin C for brightness and anti-aging',
      'brightening': 'Vitamin C and Alpha Arbutin for radiance',
      'texture': 'Niacinamide for texture refinement'
    };
    return serums[this.goal] || 'Multi-benefit serum';
  }

  getTreatmentForGoal(): string {
    const treatments: Record<string, string> = {
      'hydration': 'Hydrating essence',
      'acne': 'Salicylic Acid 2% for acne treatment',
      'anti-aging': 'Retinol 0.5% for anti-aging',
      'brightening': 'Alpha Arbutin for brightening',
      'texture': 'Lactic Acid 5% for gentle exfoliation'
    };
    return treatments[this.goal] || 'Treatment serum';
  }
}
<main>
  <section class="section">
    <div class="container" style="max-width: 800px;">
      <!-- Hero Banner -->
      <div style="text-align: center; margin-bottom: 48px;">
        <div style="display: inline-block; background: linear-gradient(135deg, #ffc6dd 0%, #ff69b4 100%); padding: 12px 24px; border-radius: 50px; margin-bottom: 16px;">
          <span style="font-size: 32px;">✨</span>
        </div>
        <h1 style="margin-bottom: 12px; background: linear-gradient(135deg, #ff1493, #ff69b4); -webkit-background-clip: text; -webkit-text-fill-color: transparent; background-clip: text;">Routine Builder</h1>
        <p class="muted" style="font-size: 18px; max-width: 600px; margin: 0 auto;">
          Answer 3 quick questions and get your personalized morning & evening skincare routine
        </p>
      </div>

      <!-- Form -->
      <div class="card" style="margin-bottom: 32px;">
        <form (submit)="generateRoutine(); $event.preventDefault()">
          <div style="margin-bottom: 20px;">
            <label><strong>Skin Type</strong></label><br />
            <select [(ngModel)]="skinType" name="skinType" required style="width: 100%; margin-top: 8px;">
              <option value="">Select your skin type</option>
              <option value="Oily">Oily</option>
              <option value="Dry">Dry</option>
              <option value="Combination">Combination</option>
              <option value="Sensitive">Sensitive</option>
            </select>
          </div>

          <div style="margin-bottom: 20px;">
            <label><strong>Main Goal</strong></label><br />
            <select [(ngModel)]="goal" name="goal" required style="width: 100%; margin-top: 8px;">
              <option value="">Select your goal</option>
              <option value="hydration">Hydration</option>
              <option value="acne">Acne / Breakouts</option>
              <option value="anti-aging">Anti-Aging</option>
              <option value="brightening">Brightening</option>
              <option value="texture">Texture / Smoothing</option>
            </select>
          </div>

          <div style="margin-bottom: 20px;">
            <label><strong>Budget</strong></label><br />
            <select [(ngModel)]="budget" name="budget" required style="width: 100%; margin-top: 8px;">
              <option value="">Select budget range</option>
              <option value="budget">Budget Friendly</option>
              <option value="mid">Mid-Range</option>
              <option value="premium">Premium</option>
            </select>
          </div>

          <button type="submit" class="btn" style="width: 100%;">
            Generate Routine
          </button>
        </form>
      </div>

      <!-- Results -->
      @if (showResults) {
        <div class="card">
          <div class="tabs">
            <button
              [class.active]="activeTab === 'morning'"
              (click)="activeTab = 'morning'"
              class="tab-button"
            >
              Morning Routine
            </button>
            <button
              [class.active]="activeTab === 'evening'"
              (click)="activeTab = 'evening'"
              class="tab-button"
            >
              Evening Routine
            </button>
          </div>

          <div class="tab-content">
            @if (activeTab === 'morning') {
              <div class="routine-steps">
                @for (step of morningRoutine; track step.step) {
                  <div class="routine-step">
                    <div class="step-number">{{ step.step }}</div>
                    <div>
                      <h4>{{ step.product }}</h4>
                      <p class="muted">{{ step.description }}</p>
                    </div>
                  </div>
                }
              </div>
            }

            @if (activeTab === 'evening') {
              <div class="routine-steps">
                @for (step of eveningRoutine; track step.step) {
                  <div class="routine-step">
                    <div class="step-number">{{ step.step }}</div>
                    <div>
                      <h4>{{ step.product }}</h4>
                      <p class="muted">{{ step.description }}</p>
                    </div>
                  </div>
                }
              </div>
            }
          </div>
        </div>
      }
    </div>
  </section>
</main>
